(ns functional-music.tab-test
  (:require [clojure.test :refer :all]
            [functional-music.tab :refer :all]))

(def everybody-hurts
  (clojure.string/split-lines
"
Guitar Tab for Everybody Hurts by REM
e|--------2-----------2------------3-----------3-----|
B|------3---2-------3---3--------0---0-------0---0---|
G|----2-------2---2-------2----0-------0---0-------0-|
D|--0-----------0------------------------------------|
A|---------------------------------------------------|
E|---------------------------3-----------3-----------|
"))

(def filter-tab-lines-output
  ["e|--------2-----------2------------3-----------3-----|" "B|------3---2-------3---3--------0---0-------0---0---|" "G|----2-------2---2-------2----0-------0---0-------0-|" "D|--0-----------0------------------------------------|" "A|---------------------------------------------------|" "E|---------------------------3-----------3-----------|"])

(deftest filter-tab-lines-test
  (is (= (filter-tab-lines everybody-hurts)
         filter-tab-lines-output)))

;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;

(def remove-format-output
  ["--------2-----------2------------3-----------3-----" "------3---2-------3---3--------0---0-------0---0---" "----2-------2---2-------2----0-------0---0-------0-" "--0-----------0------------------------------------" "---------------------------------------------------" "---------------------------3-----------3-----------"])

(deftest remove-format-test
  (is (= (remove-format filter-tab-lines-output)
         remove-format-output)))

;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;

(deftest continuous-tab-test
  (is (= (continuous-tab remove-format-output)
         ["--------2-----------2------------3-----------3-----" "------3---2-------3---3--------0---0-------0---0---" "----2-------2---2-------2----0-------0---0-------0-" "--0-----------0------------------------------------" "---------------------------------------------------" "---------------------------3-----------3-----------"])))

